      //    },
      //   })
      //   .then((response)=>{
      //     setUserlevel(response.data.current_question)
      //   })
      //   .then(() => {
      //    setloaded(true);
      //  });

       },[]);
   return (

        <div className={styles.Container}>
            <div className={styles.cont}>
            <div className={styles.TimelineContainer}>
             {isLoggedin? (<Timeline level={level} userlevel={userlevel} loaded={loaded}/>):(<><div className="show" style={{color:"white"}}>PLEASE LOG IN!</div></>)}</div>
            <div className={styles.MainContainer}>
              {isLoggedin? (<Main level={level} userlevel={userlevel} loaded={loaded} handleLevel={handleLevel} isLoggedin={isLoggedin}/>):(<Login isLoggedin={isLoggedin}/>)}
                </div>
            <div className={styles.LeadtableContainer}><LeadTable/></div>
            </div>
            </div>

    )
}

export default dashboard
