import React , {useState,useEffect}from 'react'
import LeadTable from '../components/LeadTable';
import Main from '../components/Main';
import Login from '../components/Login';
import Timeline from '../components/Timeline';
import styles from '../styles/dashboard.module.css';
import Router from "next/router";
import axios from "axios";



function dashboard() {
    const [userlevel, setUserlevel]= useState("");
    const [level,setLevel]= useState("");
    const [loaded,setloaded]= useState(false);
    const [isLoggedin,setIsLoggedin]=useState(false);

    const checkLogin = ()=>{
      if(localStorage.getItem('token') === null ){
        setIsLoggedin(false)
        console.log("1");

      }else{
          setIsLoggedin(true)
          console.log("2");
        }
      }


    const handleLevel = ()=>{
        axios
        .get(process.env.api + "/api/status")
        .then((response)=>{

         setLevel(response.data.round_questions)


        })
        axios
        .get(process.env.api + "/api/timeline",{
         headers: {
           Authorization: "Bearer " + localStorage.getItem("token"),
         },
        })
        .then((response)=>{
          setUserlevel(response.data.current_question)
        })

    }
    useEffect(()=>{

      checkLogin();
      if(!isLoggedin){
        return;
      }

        axios
        .get(process.env.api + "/api/status")
        .then((response)=>{
         setLevel(response.data.round_questions)
        })
        axios
        .get(process.env.api + "/api/timeline",{
         headers: {
           Authorization: "Bearer " + localStorage.getItem("token"),
         },
        })
        .then((response)=>{
          setUserlevel(response.data.current_question)
        })
        .then(() => {
         setloaded(true);
       });

       },[]);
   return (

        <div className={styles.Container}>
            <div className={styles.cont}>
            <div className={styles.TimelineContainer}>
             {isLoggedin? (<Timeline level={level} userlevel={userlevel} loaded={loaded}/>):(<>PLEASE LOG IN!</>)}</div>
            <div className={styles.MainContainer}>
              {isLoggedin? (<Main level={level} userlevel={userlevel} loaded={loaded} handleLevel={handleLevel}/>):(<Login/>)}
                </div>
            <div className={styles.LeadtableContainer}><LeadTable/></div>
            </div>
            </div>

    )
}

export default dashboard
